    {
      //invalid login
    }
  })
}, [])
  return (

    <div className="App">
      {user?
        (
          <div>
                <Sidebar/>
                <Chat/>
            </div>
        )
        :(
          // <p>u have not been logged in </p>
          <Login/>
        )

      }

    </div>
  );
}

export default App;
